package com.delivery.exception;

public class NotFoundException extends CatalogException {

    private static final long serialVersionUID = 8917189863056389702L;

    public NotFoundException(ErrorCode errCode) {
        super(errCode);
    }
    
    public NotFoundException(ErrorCode errCode, Throwable e) {
        super(errCode, e);
    }
    
    public NotFoundException(Throwable e) {
        super(e);
    }
}
                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                       